import React, { useState, useRef, useEffect, forwardRef } from 'react';
import './Accordion.css';

interface AccordionItemProps {
  title: string;
  content: string;
}

const AccordionItem: React.FC<AccordionItemProps> = ({ title, content }) => {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef<HTMLDivElement>(null);

  const toggleAccordion = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (contentRef.current) {
      contentRef.current.style.maxHeight = isOpen ? `${contentRef.current.scrollHeight}px` : '0px';
    }
  }, [isOpen]);

  return (
    <div className="accordion-item">
      <button className={`accordion-button ${isOpen ? 'active' : ''}`} onClick={toggleAccordion}>
        {title}
        {/* <span className="accordion-button-text">{title}</span> */}
        <span className={`icon ${isOpen ? 'open' : ''}`}>+</span>
      </button>
      <div ref={contentRef} className={`accordion-content ${isOpen ? 'open' : ''}`}>
        <p>{content}</p>
      </div>
    </div>
  );
};


const Accordion= forwardRef<HTMLDivElement>((props, ref) => {
  const items = [
    {
      title: 'Подтверждено: сознание наших соотечественников не замутнено пропагандой?',
